    const divListado = ref(null);
    const getMoreShips = e => {
      if (divListado.value.getBoundingClientRect().bottom < window.innerHeight) {
        store.dispatch("changeShipPageNumber", store.state.pageShip + 1);
        if (store.state.stopGettingShips === false && !store.state.pagesLoaded.includes(store.state.pageShip + 1)) {
          store.dispatch("changeShipPageNumber", store.state.pageShip + 1);
          showShipsList();
        }
      }
    };
    onMounted(() => {
      window.addEventListener("scroll", getMoreShips);
    });
    onUnmounted(() => {
