import { createStore } from 'vuex'

export default createStore({
  state: {
    mensaje:"esto es prueba",
    pageShip:1,
    ships: [],
  getters: {
    countShips: (state) => state.ships.length,
  },
  mutations: {
    loadShip: (state, apiResponse) => (state.ships = apiResponse),
    },
  },
  actions: {
    getShipList(state){
      fetch(`https://swapi.dev/api/starships/?page=${state.pageShip}`)
            .then(response => response.json())
            .then(json => {
              console.log(json.value)
              state.ships=json.value;
              console.log(state.ships)
            })
            .catch((error) => console.log('Ocurrió un error: ', error));
          }
  },
  modules: {
  }
})
