import { createStore } from 'vuex'

export default createStore({
  state: {
    mensaje:"esto es prueba",
    pageShip:1,
    ships: [],
  getters: {
    countShips: (state) => state.ships.length,
  },
  mutations: {
    aumentar2:  "null",
    loadShip (state, apiResponse){
      state.ships = [...state.ships, apiResponse.result]
      },
    changeShipPage (state, newPage){
      state.pageShip = newPage.page
    },
    aumentar (state) {
      state.pageShip++
    },
    },
  },
  actions: {
    getShipList(context, page=context.state.pageShip){
      console.log(page)
      fetch(`https://swapi.dev/api/starships/?page=${page}`)
            .then(response => response.json())
            .then(json => {
              console.log(json.results)
              // context.commit("loadShip",{result: json.results})
              console.log(context.state.ships)
              // context.commit("changeShipPage",{
              //   page: page+=1
              // })
              console.log(context.state.pageShip)

            })
            .catch((error) => console.log('Ocurrió un error: ', error));
          }
  },
  modules: {
  }
})
