    const store = useStore();
    const showShipsList = page => {
      store.dispatch("getShipList", page);
      // store.dispatch("changeShipPageNumber")
    };

    const numPage = ref(1);
    const stopGettingShips = ref(false);
    const pagesLoaded = ref([]);
    onBeforeMount(() => {
