    const divListado = ref(null);
    const getMoreShips = e => {
      if (divListado.value.getBoundingClientRect().bottom < window.innerHeight && !stopGettingShips.value) {
        // pagesLoaded.value=pagesLoaded.value.sort();
        // if(store.state.stopGettingShips===false && !pagesLoaded.value.includes(store.state.pageShip+1)){
        if (!pagesLoaded.value.includes(numPage.value + 1)) {
          // store.dispatch("changeShipPageNumber", store.state.pageShip+1)
          pagesLoaded.value.push(numPage.value + 1);
          stopGettingShips.value = true;
          numPage.value++;
          showShipsList(numPage.value);
          setTimeout(() => {
            stopGettingShips.value = false;
          }, 800);
