import { createStore } from 'vuex'

export default createStore({
  state: {
    mensaje:"esto es prueba",
    pageShip:1,
    ships: [],
    stopGettingShips: false,
  },
  mutations: {
    loadShip (state, apiResponse){
      state.ships = [...state.ships, ...apiResponse]
      },
    changeShipPage (state, newPage){
      state.pageShip = newPage
    },
    stopGettingShips (state){
      state.stopGettingShips = true
    },
    aumentar (state) {
      state.pageShip++
    },
  },
  getters: {
    countShips (state){
         return state.ships.length
     },
  },
  actions: {
    getShipList(context, page=context.state.pageShip){
      fetch(`https://swapi.dev/api/starships/?page=${page}`)
            .then( response => {
              if (!response.ok) {
                throw response.status

              } else{
                return response.json()
              }
              })
            .then(json => {
              context.commit("loadShip",json.results)
              context.commit("changeShipPage",page+=1)
              if (json.count<=context.getters.countShips){
                context.commit("stopGettingShips")
              }

            })
            .catch((error) => {
              console.log('Ocurrió un error: ', error)
              if (error=="TypeError: Failed to fetch"){
                fetch(`https://swapi.py4e.com/api/starships/?page=${page}`)
                .then( response => {
                  if (!response.ok) { 
                    throw response.status

                  } else{
                    console.log("Fetch alternativo")
                    return response.json()
                  }
                  })
                .then(json => {
                  context.commit("loadShip",json.results)
                  context.commit("changeShipPage",page+=1)

                })
                .catch((error) => {
                  console.log('Ocurrió un error: ', error)
                  if (error=="TypeError: Failed to fetch"){
                    console.log("Falló el fetch nuevamente")
                  }
                });
              }
            });
          },
      aumentarAction({ commit }){
        commit('aumentar')
      },
  },
  modules: {
  }
})
