import { ref, watch } from "vue";
import { useStore } from 'vuex';
export default {
  __name: 'LoginView',
  setup(__props, {
    expose
  }) {
    expose();
    const store = useStore();

    // import { useRouter, useRoute } from 'vue-router'

    // const router = useRouter()
    // const route = useRoute()
    const userName = ref("");
    const userEmail = ref("");
    const userPass = ref("");
    const passWarnSpan = ref("");
    const emailWarnSpan = ref("");
    const btnSubmitActive = ref(false);
    const registerUser = () => {
      if (store.state.usersRegistered.includes(userEmail.value)) {
        this.$refs.userEmail.focus();
        emailWarnSpan.value = "Ya existe un usuario registrado con ese email";
      }
    };
    watch([userName, userEmail, userPass], ([newUserName, newUserEmail, newUserPass]) => {
      passWarnSpan.value = newUserPass.length > 1 && newUserPass.length < 4 ? "La contraseña debe contener mínimamente 4 caracteres" : "";
      emailWarnSpan.value = newUserEmail.length > 1 && !newUserEmail.includes('@') ? "El email debe ser una dirección válida" : "";
      if (newUserName.length > 1 && newUserPass.length >= 4 && newUserEmail.length > 4 && newUserEmail.includes('@')) {
        btnSubmitActive.value = true;
      } else btnSubmitActive.value = false;
    });
    const __returned__ = {
      store,
      userName,
      userEmail,
      userPass,
      passWarnSpan,
      emailWarnSpan,
      btnSubmitActive,
      registerUser,
      ref,
      watch,
      useStore
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
