import { createRouter, createWebHashHistory } from 'vue-router'
import HomeView from '../views/HomeView.vue'
// import { useStore } from 'vuex'
// const store = useStore()
const router = createRouter({
  history: createWebHashHistory(),
  routes
})

router.beforeEach((to, from, next)=>{
  document.title=`Starwars: ${to.meta.title}`;
  next();
})

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView,
    meta:{
      title: "Home",
    }
  },
  {
    path: '/naves',
    name: 'naves',
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () => import(/* webpackChunkName: "naves" */ '../views/NavesView.vue'),
    meta:{
      title: "Naves",
    },
    beforeEnter: (to, from, next)=>{
      // if (!store.state.authenticated){
      if (!router.app.$store.state.authenticated){
        next("/login");
      } else{
        next();
      }
    }
  },
  {
    path: '/naves/:naveName', 
    name: 'nave',
    component: () => import(/* webpackChunkName: "nave" */ '../views/NaveView.vue'),
    meta:{
      title: "Nave",
    }
  },
  {
    path: '/actors',
    name: 'actors',
    component: () => import(/* webpackChunkName: "actors" */ '../views/ActorsView.vue'),
    meta:{
      title: "Actores",
    }
  },
  {
    path: '/register',
    name: 'register',
    component: () => import(/* webpackChunkName: "register" */ '../views/RegisterView.vue')
  },
  {
    path: '/login',
    name: 'login',
    component: () => import(/* webpackChunkName: "login" */ '../views/LoginView.vue')
  },
  {
    path: '/logout',
    name: 'logout',
    component: () => import(/* webpackChunkName: "logout" */ '../views/LogoutView.vue')
  },
]

export default router
