import { createStore } from 'vuex'

export default createStore({
  state: {
    mensaje:"esto es prueba",
    pageShip:1,
    ships: [],
  },
  mutations: {
    loadShip (state, apiResponse){
      state.ships = [...state.ships, ...apiResponse]
      },
    changeShipPage (state, newPage){
      state.pageShip = newPage
    },
    aumentar (state) {
      state.pageShip++
    },
  },
  getters: {
    countShips (state){
         return state.ships.length
     },
  },
  actions: {
    getShipList(context, page=context.state.pageShip){
      fetch(`https://swafdsfdspi.dev/api/starships/?page=${page}`)
            .then( response => {
              if (!response.ok) {
                console.log(response.status)  
                throw response

              } else{
                return response.json()
              }
              })
            .then(json => {
              context.commit("loadShip",json.results)
              context.commit("changeShipPage",page+=1)

            })
            .catch((error) => console.log('Ocurrió un error: ', error));
          },
      aumentarAction({ commit }){
        commit('aumentar')
      },
  },
  modules: {
  }
})
