import { createStore } from 'vuex'

export default createStore({
  state: {
    mensaje:"esto es prueba",
    pageShip:1,
    ships: [],
  getters: {
    countShips: (state) => state.ships.length,
  },
  mutations: {
    loadShip: (state, apiResponse) => (state.ships = apiResponse),
    changeShipPage: (state, newPage) => (state.pageShip = newPage),
    },
  },
  actions: {
    getShipList(state, page=1){
      fetch(`https://swapi.dev/api/starships/?page=${page}`)
            .then(response => response.json())
            .then(json => {
              console.log(json.value)
              state.commit("loadShip",json.value)
              state.commit("changeShipPage",page+=1)
              console.log(state.ships)
            })
            .catch((error) => console.log('Ocurrió un error: ', error));
          }
  },
  modules: {
  }
})
