import { ref } from 'vue';
import { useRoute, useRouter } from 'vue-router';
import { useStore } from 'vuex';
import Pilotos from '@/components/Pilotos.vue';
// import NaveFile from '@/components/NaveFile.vue'

export default {
  __name: 'NaveView',
  setup(__props, {
    expose
  }) {
    expose();

    // @ is an alias to /src
    const route = useRoute();
    const router = useRouter();
    const store = useStore();
    const nameShip = ref(route.params.naveName);
    const dataNave = store.state.ships.find(ship => ship.name == nameShip.value);
    const pilotos = ref(dataNave.pilots);
    if (store.getters.countShips == 0) {
      router.push({
        name: 'naves'
      }); //si accede directo o reload, redirect a listado (porque no tenemos propiedad id para hacer fetch a url específica de nave)
    }

    const __returned__ = {
      route,
      router,
      store,
      nameShip,
      dataNave,
      pilotos,
      ref,
      useRoute,
      useRouter,
      useStore,
      Pilotos
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
