    const store = useStore();
    const showShipsList = () => {
      store.dispatch("getShipList");
      // store.dispatch("changeShipPageNumber")
    };

    onBeforeMount(() => {
      if (store.state.pageShip == 1) {
        showShipsList();
      }
    });
    const numPage = ref(1);
    const pagesLoaded = ref([]);

    // const aumentarPage=()=>{
    //   numPage.value++;
    //   if(!store.state.pagesLoaded.includes(numPage.value)){
    //     store.dispatch("changeShipPageNumber", numPage.value)
    //     showShipsList();
