    const divListado = ref(null);
    const getMoreShips = e => {
      if (divListado.value.getBoundingClientRect().bottom < window.innerHeight) {
        // pagesLoaded.value=pagesLoaded.value.sort();
        let clone = [...pagesLoaded.value];
        let lastPage = clone.sort()[clone.length - 1];
        if (store.state.pageShip + 1 - lastPage == 1) {
          if (store.state.stopGettingShips === false && !pagesLoaded.value.includes(store.state.pageShip + 1)) {
            pagesLoaded.value.push(store.state.pageShip + 1);
            store.dispatch("changeShipPageNumber", store.state.pageShip + 1);
            showShipsList();
          } else {
            let lastPage = pagesLoaded.value[pagesLoaded.value.length - 1];
            store.dispatch("changeShipPageNumber", lastPage);
          }
        } else if (store.state.stopGettingShips === true) {
          let pageMasAlta = Math.max(...store.state.pagesLoaded);
          store.dispatch("changeShipPageNumber", pageMasAlta);
        } else {
          store.dispatch("changeShipPageNumber", lastPage + 1);
        }
      }
    };
    onMounted(() => {
      window.addEventListener("scroll", getMoreShips);
    });
    onUnmounted(() => {
      window.removeEventListener("scroll", getMoreShips);
    });
    const __returned__ = {
